function createRipple(e){const t=e.currentTarget,n=document.createElement("span"),s=Math.max(t.clientWidth,t.clientHeight),l=s/2;n.style.width=n.style.height=`${s}px`,n.style.left=e.clientX-t.offsetLeft-l+"px",n.style.top=e.clientY-t.offsetTop-l+"px",n.classList.add("ripple");const c=t.getElementsByClassName("ripple")[0];c&&c.remove(),t.appendChild(n)}const buttons=document.getElementsByClassName("btn");for(const e of buttons)e.addEventListener("click",createRipple);